class Pager
{
    const DELIMITER = '<div class="pagination__separator">&hellip;</div>';

    protected $firstLink;
    protected $otherLinks;
    protected $pages;
    protected $actualPage;
    protected $queryParams;
    protected $loadMoreButton;

    /**
     * Pager constructor.
     */
    public function __construct($firstLink, $otherLinks, $pages, $actualPage, $queryParam = array(), $loadMore = false)
    {
        $this->firstLink = $firstLink;
        $this->queryParams = http_build_query($queryParam);
        $this->loadMoreButton = $loadMore;

        if(count($queryParam) > 0)
        {
            if(strpos($this->firstLink, '?') !== false || strpos($this->firstLink, '&') !== false)
            {
                $this->firstLink .= '&' . $this->queryParams;
            }
            else
            {
                $this->firstLink .= '?' . $this->queryParams;
            }
        }

        if(strpos($otherLinks, '?') !== false || strpos($otherLinks, '&') !== false)
        {

            $this->otherLinks = $otherLinks . (substr($otherLinks,-1) === '&' ? '' : '&'). 'page=';

            if(count($queryParam) > 0)
            {
                $this->queryParams = '&' . $this->queryParams;

            }else{
                $this->queryParams = '';
            }
        }
        else
        {
            $this->otherLinks = $otherLinks . (substr($otherLinks,-1) === '/' ? '' : '/'). 'page-';

            if(count($queryParam) > 0)
            {
                $this->queryParams = '?' . $this->queryParams;
            }else{
                $this->queryParams = '';
            }
        }

        $this->pages = $pages;
        $this->actualPage = $actualPage;


    }

    public function render($showFull = false, $elementClass = null, $backPage = 'Předchozí stránka', $nextPage = 'Další stránka')
    {
        $block = '';

        if($this->pages <= 10)
        {
            $block .= $this->getHTML(1);
            for($i = 2;$i <= $this->pages;$i++)
            {
                $block .= $this->getHTML($i);
            }
        }
        else
        {
            $block .= $this->getHTML(1);

            $bottomLimit = $this->actualPage - 3;
            $topLimit = $this->actualPage + 4;

            if($bottomLimit >= 3)
            {
                $block .= self::DELIMITER;
            }
            else
            {
                $bottomLimit = 2;
            }

            $showTopDelimiter = false;
            if($topLimit <= $this->pages)
            {
                $showTopDelimiter = true;
            }
            else
            {
                $topLimit = $this->pages;
            }

            for($i = $bottomLimit;$i < $topLimit;$i++)
            {
                $block .= $this->getHTML($i);
            }

            if($showTopDelimiter)
            {
                $block .= self::DELIMITER;
            }

            $block .= $this->getHTML($this->pages);
        }

        if($showFull)
        {
            $preBlock = '<div class="pagination">';
            if($this->actualPage == 2)
            {
                $link = LeadingSlash::checkLink("{$this->firstLink}");
               // $preBlock .= "<li class=\"list-pagination__item list-pagination__item--prev\"><a href=\"{$link}\" class=\"list-pagination__item__in\">{$backPage}</a></li>";

                $preBlock .= "<a href='{$link}' class='pagination__arrow pagination__arrow--prev'>";
                $preBlock .=    "<span class='icon icon--icon-prev'>";
                $preBlock .=        "<svg class='icon__svg'><use xlink:href='#icon-prev'></use></svg>";
                $preBlock .=    "</span>";
                $preBlock .= "</a>";
            }
            elseif($this->actualPage > 2)
            {
                $page = $this->actualPage - 1;
                $link = LeadingSlash::checkLink("{$this->otherLinks}{$page}{$this->queryParams}");
                //$preBlock .= "<li class=\"list-pagination__item list-pagination__item--prev\"><a href=\"{$link}\" class=\"list-pagination__item__in\" >{$backPage}</a></li>";

                $preBlock .= "<a href='{$link}' class='pagination__arrow pagination__arrow--prev'>";
                $preBlock .=    "<span class='icon icon--icon-prev'>";
                $preBlock .=        "<svg class='icon__svg'><use xlink:href='#icon-prev'></use></svg>";
                $preBlock .=    "</span>";
                $preBlock .= "</a>";
            }

            $preBlock .= "<div class='pagination__pager'>";
            $preBlock .= $block;
            $preBlock .= '</div>';
            if($this->actualPage != $this->pages && $this->pages > 1)
            {
                $page = $this->actualPage + 1;
                $link = LeadingSlash::checkLink("{$this->otherLinks}{$page}{$this->queryParams}");
               // $preBlock .= "<li class=\"list-pagination__item list-pagination__item--next\"><a href=\"{$link}\" class=\"list-pagination__item__in\" >{$nextPage}</a></li>";

                $preBlock .= "<a href='{$link}' class='pagination__arrow pagination__arrow--next'>";
                $preBlock .=    "<span class='icon icon--icon-next'>";
                $preBlock .=        "<svg class='icon__svg'><use xlink:href='#icon-next'></use></svg>";
                $preBlock .=    "</span>";
                $preBlock .= "</a>";
            }
            $preBlock .= '</div>';

            return $preBlock;
        }

        return $block;
    }

    private function getHTML($actualPage)
    {
       $ret = '';
        $link = $actualPage == 1? $this->firstLink : "{$this->otherLinks}{$actualPage}{$this->queryParams}";
        $link = LeadingSlash::checkLink($link);
        if($actualPage == $this->actualPage)
        {
           // $ret .= "<span class=\"list-pagination__item__in list-pagination__item__in--current\">{$actualPage}</span>";
            $ret .= "<a href='{$link}' class='is-active'>{$actualPage}</a>";
        }
        else
        {
            $ret .= "<a href='{$link}'>{$actualPage}</a>";
            //$ret .= "<li class=\"list-pagination__item\"><a href=\"{$link}\" class=\"list-pagination__item__in\">{$actualPage}</a></li>";
        }


        return $ret;
    }

    public function getNextPage()
    {
        $page = $this->actualPage + 1;
        if($page >= $this->pages){
            return '';
        }else{
            return LeadingSlash::checkLink("{$this->otherLinks}{$page}{$this->queryParams}");
        }
    }
}

class LeadingSlash
{
    public static function checkLink($link)
    {
//        if(strpos($link, '?') === false && strpos($link, '&') === false && substr($link, -1) !== '/'){
//            return "{$link}/";
//        }

        return $link;
    }
}

