    public static function printSvg($propertyKey, $prefixIds = false)
    {
        if(isset(self::$propertyCSSIcons[$propertyKey]))
        {
            if($prefixIds){
                $svg = self::$propertyCSSIcons[$propertyKey];
                $randomIdPrefix = substr(uniqid('', true), -5);
                $svg = str_replace("id='","id='{$randomIdPrefix}",$svg);
                $svg = str_replace('id="',"id=\"{$randomIdPrefix}",$svg);
                $svg = str_replace('xlink:href="#',"xlink:href=\"#{$randomIdPrefix}",$svg);
                $svg = str_replace("xlink:href='#","xlink:href='#{$randomIdPrefix}",$svg);
                return $svg;
            }else{
                echo self::$propertyCSSIcons[$propertyKey];
            }
        }

        echo '';
    }
}

class SearchFormMocker
{
    public static function createMockForm($sendButtonText, $parametes) {
        echo '<form action="/mlift/services/save_form.php" method="post">';

        foreach($parametes as $key => $values){
            if(is_array($values)){
                foreach($values as $value){
                    echo "<input type='hidden' name='sql[{$key}][]' value='{$value}'>";
                }
            }else{
                echo "<input type='hidden' name='sql[{$key}]' value='{$values}'>";
            }
        }

        echo "<button type=\"submit\" class=\"button\">{$sendButtonText}</button>";
        echo '</form>';
    }
}

class ScoreFormatter
{
    public static function formate($score){
        $showValue = floor($score);
        $baseValue = $score - $showValue;

        if($baseValue >= 0.5){
            $showValue += 0.5;
        }

        return $showValue;
    }
}

class GeoJson
{
    private $points;

    /**
     * GeoJson constructor.
     */
    public function __construct()
    {
        $this->points = array();
    }

    public function addPoint($lat, $lng, $properties)
    {
        $this->points[] = array(
            'type' => 'Feature',
            'properties' => $properties,
            'geometry' => array(
                'type' => 'Point',
                'coordinates' => array(
                    $lng,
                    $lat
                )
            )
        );
    }

    public function escapeJSString($string, $encoding = 'UTF-8')
    {
        if ($encoding != 'UTF-8')
            $string = iconv($encoding, 'UTF-8', $string);
        $flags = JSON_HEX_TAG|JSON_HEX_AMP|JSON_HEX_APOS|JSON_HEX_QUOT|JSON_UNESCAPED_SLASHES;
        $string = substr(json_encode($string, $flags), 1, -1);
        return "'$string'";
    }

    public function getHtml()
    {
        return $this->escapeJSString(json_encode(array(
            'type' => 'FeatureCollection',
            'features' => $this->points
        ), JSON_HEX_TAG));
    }
}

